mapper.select:
SELECT is_quiz_participate
FROM users
WHERE user_id=#{value}

SELECT quiz_id
FROM users
WHERE user_id=#{value}


SELECT quiz_content, select1, select2, select3, select4
FROM quiz
WHERE quiz_id=#{value}

SELECT answer
FROM quiz
WHERE quiz_id=#{value}

mapper.update:
UPDATE users
SET is_quiz_participate='1' , quiz_id = quiz_id+1
WHERE user_id=#{value}

